.flowtime ul li:before,.flowtime dl dt:before {
	content:"\2022";
	text-align:right;
	color:#fd0;
	margin-right:.5rem;
	display:inline-block;
	position:absolute;
	left:-1rem
}
.flowtime ol {
	counter-reset:list
