	box-shadow:inset 0 3px 5px rgba(0,0,0,0.125)
}
.write-ok {
	position:fixed;
	top:0;
	left:0;
	width:100%;
	height:100%;
	z-index:999;
	background-color:#FCC7E2;
	background-attachment:fixed;
	display:none
}
.write-ok .write-box {
	position:relative;
	margin:15% auto;
	padding:20px;
	text-align:center;
	width:50%;
	height:45%;
	background:#fff;
	box-shadow:0px 0px 10px 0px #F7B5DC;
	border-radius:8px;
}
.write-box h2 {
	font-size:2em;
	line-height:2.5em;
	color:#5EAF6F;
	text-shadow:1px 1px 3px #D3D3D3;
}
.write-box p {
	line-height:2em;
	white-space:pre-wrap;
	word-wrap:break-word
}
.write-box p u,.write-box p a {
	color:#007ed9;
}
.write-box p u:hover,.write-box p a:hover {
	color:#EB56AF;
}
.write-box p span {
	display:inline-block;
	min-width:1em;
	line-height:1.3em;
	padding:0 2px;
	margin:0 2px;
	outline:1px solid #ccc
}
.write-box p i {
	position:relative;
	top:-1px;
	font-size:0.8em;
	color:#806D6D;
	padding:3px 4px;
	background:#F7F7F7;
	border:1px solid #E7E7E7;
	border-radius:6px;
	display:none;
}
.write-box p small {
	line-height:3em;
	font-size:90%;
	color:#999;
}
.write-box #back {
	color:#888;
	margin-right:5px
}
.write-box button {
	color:#fff;
	font-size:1.2em;
	display:inline-block;
	background-color:#428bca;
	padding:13px 14px;
	margin-top:1em;
	border:1px solid #357ebd;
	border-radius:4px;
	outline:none;
	cursor:pointer
}
.write-box button:hover {
